@import "reset";
@import "variables";
// @import "infoSection";
@import "home";
// @import "work";


* {
    box-sizing: border-box;
}

html, 
body {
    font-size: 18px;
    background: $color1;
    font-family: 'Barlow Semi Condensed', sans-serif;
    height: 100%;
}
.menu-icon {
    position: fixed;
    right: 34px;
    top: 34px;
    padding: 20px;
    z-index: 11;

    .fa-solid {
        font-size: 2rem;
        color: $color1;
    }
}
.primary-btn {
    text-decoration: none;
    background: $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color3;
    padding: 15px 30px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    margin-right: 15px;
    transition: $btnTransitions;
    &:hover {
        background-color: $color2;
    }
}

.ghost-btn {
    text-decoration: none;
    border: 4px solid $color1;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;
    text-align: center;
    color: $color1;
    padding: 12px 27px;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: 3px;
    transition: $btnTransitions;
    &:hover {
        border-color: $color2;
        color: $color2;
    }
}

#menu {
    background: $color1;
    position: fixed;
    width: 100%;
    top: 0;
    bottom: 0;
    transform: translate3d(-100%,0,0);
    z-index: 10;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 1;
    transition: all .5s ease-in-out;
    nav {
        width: 400px;
        text-align: center;
        a {
            font-size: 2.3rem;
            padding: 1rem;
            display: block;
            text-decoration: none;
            color: #ffffff;
            &:hover {
            color: $color5; 
            }
        }
    }
}

.menu-active {
    .menu-icon {
        .fa-solid {
            color: #ffffff;
        }
    }
    #menu {
        width: 100%;
        top:0;
        bottom: 0;
        transform: translate3d(0,0,0);
    }
}
